require "data_uri"

RSpec.describe DataUri do
  subject(:data_uri) { DataUri.new(uri) }

  before do
    allow(File).to receive(:open).and_yield(file)
    allow(file).to receive(:print).and_call_original
  end
  let(:file) { StringIO.new }

  context "with base64 data" do
    let(:uri) { "data:image/png;base64," }

    it "decodes the image data and writes the result to disk" do
      base64_data = ""

      data_uri.write_to_file

      expect(file).to have_received(:print)
      expect(file).not_to have_received(:print).with(base64_data)
    end

    it "creates a file name from the data itself" do
      digest_of_data = "544c7a60c7e34728383b74b57d7ebe658fecb1cab4c4757dbb43e38c0f7e8de9"

      expect(data_uri.name).to match(/#{digest_of_data}\.png/)
    end
  end

  context "with text (non-base64) data" do
    let(:uri) { "data:image/svg+xml,<svg><circle cx='40' cy='40' r='24' style='fill:#00cc00'/></svg>" }

    it "writes the text to disk straight" do
      text_data = "<svg><circle cx='40' cy='40' r='24' style='fill:#00cc00'/></svg>"

      data_uri.write_to_file

      expect(file).to have_received(:print).with(text_data)
    end
  end
end
